$conn = oci_connect('keesh', 'Cc', '192.168.9.210/orcl', 'AL32UTF8');

if (!$conn) {
  $err = oci_error();
  trigger_error(htmlentities($err['message'], ENT_QUOTES), E_USER_ERROR);
  return;
}

echo "connected";

$query = oci_parse($conn, 'select * from TABLE1');

oci_execute($query);

while (($row=oci_fetch_array($query, OCI_BOTH)) != false) {
  echo $row[0];
}

oci_free_statement($query);
oci_close($query);

return;


echo " insert into";
$insert = oci_parse($conn, 
                    "INSERT INTO TABLE2 (ID, NAME, BIRTHDAY, PHONE) "
                    ."VALUES(:ID, :NAME, to_date(:BIRTHDAY,'yyyy-mm-dd'), :PHONE)");

$ID       = 2;
$name     = '春晖';
$birthday = '1916-12-03';
$phone    = '18500183080';

oci_bind_by_name($insert, ':ID', $ID);
oci_bind_by_name($insert, ':NAME', $name);
oci_bind_by_name($insert, ':BIRTHDAY', $birthday);
oci_bind_by_name($insert, ':PHONE', $phone);

$r = oci_execute($insert);  // executes and commits

if (!$r) {
  $err = oci_error();
  trigger_error(htmlentities($err['message'], ENT_QUOTES), E_USER_ERROR);
  die;    
}

print "One row inserted";

oci_free_statement($insert);
oci_close($insert);

return;
