    // super.key,
    required this.title,
    required this.subtitle,
    this.leadingIcon = Icons.label,
    this.trailingIcon = Icons.add_shopping_cart,
    this.listTileColor = Colors.black12,
    this.iconColor = Colors.red
  });

  @override
