import 'package:flutter/material.dart';

class FormInput extends StatefulWidget {
  const FormInput({super.key});

  @override
  State<FormInput> createState() => _FormInputState();
}

class _FormInputState extends State<FormInput> {
  var _productName;

  final _productController = TextEditingController();

  @override
  void initState() {
    super.initState();

    _productController.addListener(updateText);
  }

  @override
  void dispose() {
    _productController.dispose();
    super.dispose();
  }

  void updateText() {
    setState(() {
      _productName = _productController.text;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      extendBodyBehindAppBar: false,
      backgroundColor: Colors.white,
      appBar: AppBar(
        elevation: 0,
        backgroundColor: Colors.blueAccent,
        // titleSpacing: 10,
        iconTheme: const IconThemeData(color: Colors.white),
        title: Text(
          "FormInput".toString(),
          style: const TextStyle(color: Colors.white),
        ),
      ),
      body: Container(
        padding: const EdgeInsets.all(20),
        child: ListView(
          children: [
            // const TextField(
            //     decoration: InputDecoration(
            //   hintText: "Product Name",
            //   prefixIcon: Icon(Icons.verified_user_outlined),
            // )),
            TextFormField(
              controller: _productController,
              decoration: const InputDecoration(
                  labelText: "Product Name",
                  prefixIcon: Icon(Icons.verified_user_outlined),
                  border: OutlineInputBorder()),
            ),
            const SizedBox(
              height: 40,
            ),
            buildOutlinedButton(context)
          ],
        ),
      ),
    );
  }

  OutlinedButton buildOutlinedButton(BuildContext context) {
    return OutlinedButton(
      onPressed: () {
        Navigator.push(context, MaterialPageRoute(builder: (context) {
          return DetailsForm(
            productName: _productName,
          );
        }));
      },
      style: OutlinedButton.styleFrom(minimumSize: const Size(200, 50)),
      child: const Text(
        "SubmitForm",
        style: TextStyle(fontSize: 18),
      ),
    );
  }
}

class DetailsForm extends StatelessWidget {
  DetailsForm({super.key, required this.productName});

  String productName;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      extendBodyBehindAppBar: false,
      backgroundColor: Colors.white,
      appBar: AppBar(
        elevation: 0,
        backgroundColor: Colors.blueAccent,
        titleSpacing: 0,
        iconTheme: const IconThemeData(color: Colors.white),
        // leading: null,
        title: Text(
          "Details".toString(),
          style: const TextStyle(color: Colors.white),
        ),
      ),
      body: Center(
        child: Text("Product Name: $productName"),
      ),
    );
  }
}


