import 'package:flutter/widgets.dart';

enum ProductTypeEnum { Downloadable, Deliverable }

class MyRadioButton extends StatefulWidget {
  const MyRadioButton({super.key});

  @override
  State<MyRadioButton> createState() => _MyRadioButtonState();
}

class _MyRadioButtonState extends State<MyRadioButton> {
  var _productName;
  var _productDeacription;
  bool? _checkBoxVal = true;
  bool? _listTileCheckBoxVal = true;
  ProductTypeEnum? _productTypeEnum;

  final _productNameController = TextEditingController();
  final _productDescriptionController = TextEditingController();

  @override
  void initState() {
    super.initState();

    _productNameController.addListener(updateText);
    _productDescriptionController.addListener(updateDescription);
  }

  @override
  void dispose() {
    _productNameController.dispose();
    _productDescriptionController.dispose();
    super.dispose();
  }

  void updateText() {
    setState(() {
      _productName = _productNameController.text;
    });
  }

  void updateDescription() {
    setState(() {
      _productDeacription = _productDescriptionController.text;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
        extendBodyBehindAppBar: false,
        backgroundColor: Colors.white,
        appBar: AppBar(
          elevation: 0,
          backgroundColor: Colors.blueAccent,
          // titleSpacing: 10,
          iconTheme: const IconThemeData(color: Colors.white),
          title: Text(
            "MyRadioButton".toString(),
            style: const TextStyle(color: Colors.white),
          ),
        ),
        body: Container(
          padding: const EdgeInsets.all(20),
          child: ListView(
            children: <Widget>[
              const Align(
                  alignment: Alignment.centerLeft,
                  child: Icon(
                    Icons.account_balance_wallet,
                    size: 40,
                    color: Colors.cyan,
                  )),
              const SizedBox(
                height: 5,
              ),
              const Text(
                "Product",
                style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
              ),
              const SizedBox(
                height: 5,
              ),
              const Text(
                "Add product details in the from below",
                style: TextStyle(fontSize: 18, fontWeight: FontWeight.normal),
              ),
              const SizedBox(
                height: 35,
              ),
              TextFormField(
                controller: _productNameController,
                decoration: const InputDecoration(
                    labelText: "Product Name",
                    prefixIcon: Icon(
                      Icons.near_me,
                      color: Colors.orangeAccent,
                    ),
                    border: OutlineInputBorder()),
              ),
              const SizedBox(
                height: 25,
              ),
              TextFormField(
                controller: _productDescriptionController,
                decoration: const InputDecoration(
                    labelText: "Product Description",
                    prefixIcon: Icon(Icons.accessibility_new_sharp,
                        color: Colors.orangeAccent),
                    border: OutlineInputBorder()),
              ),
              const SizedBox(
                height: 25,
              ),
              ListTileTheme(
                tileColor: Colors.lightBlueAccent,
                horizontalTitleGap: 0,
                // contentPadding: const EdgeInsets.all(0),
                child: CheckboxListTile(
                    activeColor: Colors.red,
                    controlAffinity: ListTileControlAffinity.leading,
                    // materialTapTargetSize: MaterialTapTargetSize.shrinkWrap,
                    visualDensity: VisualDensity.compact,
                    // secondary: const Icon(Icons.drive_eta),
                    contentPadding: EdgeInsets.zero,
                    value: _listTileCheckBoxVal,
                    title: const Text(
                      "Top Product",
                      // style: TextStyle(backgroundColor: Colors.red),
                    ),
                    onChanged: (val) {
                      setState(() {
                        _listTileCheckBoxVal = val;
                      });
                    }),
              ),
              const SizedBox(
                height: 5,
              ),
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  Expanded(
                    child: Container(
                      color: Colors.lightBlueAccent,
                      child: Row(
                        children: [
                          ListTileTheme(
                              tileColor: Colors.lightBlueAccent,
                              horizontalTitleGap: 0,
                              child: Checkbox(
                                  activeColor: Colors.red,
                                  visualDensity: VisualDensity.compact,
                                  value: true,
                                  onChanged: (val) {})),
                          const Text("Deliverable"),
                        ],
                      ),
                    ),
                  ),
                  Expanded(
                    child: Container(
                      margin: const EdgeInsets.only(left: 5),
                      color: Colors.lightBlueAccent,
                      child: Row(
                        children: [
                          ListTileTheme(
                              tileColor: Colors.lightBlueAccent,
                              horizontalTitleGap: 0,
                              child: Checkbox(
                                  activeColor: Colors.red,
                                  visualDensity: VisualDensity.compact,
                                  value: true,
                                  onChanged: (val) {})),
                          const Text("Downloadable"),
                        ],
                      ),
                    ),
                  ),
                ],
              ),
              const SizedBox(
                height: 15,
              ),
              buildRadio(context, ProductTypeEnum.Deliverable, 0),
              const SizedBox(
                height: 5,
              ),
              Row(
                children: <Widget>[
                  Expanded(
                    child: ListTileTheme(
                      horizontalTitleGap: 0,
                      child: RadioListTile(
                          dense: true,
                          tileColor: Colors.deepPurple.shade50,
                          contentPadding: const EdgeInsets.all(0),
                          shape: RoundedRectangleBorder(
                            borderRadius: BorderRadius.circular(5),
                          ),
                          title: Text(ProductTypeEnum.Downloadable.name),
                          value: ProductTypeEnum.Downloadable,
                          groupValue: _productTypeEnum,
                          onChanged: (val) {
                            setState(() {
                              _productTypeEnum = ProductTypeEnum.Downloadable;
                            });
                          }),
                    ),
                  ),
                  const SizedBox(width: 5),
                  Expanded(
                    child: ListTileTheme(
                      horizontalTitleGap: 0,
                      child: RadioListTile(
                          dense: true,
                          tileColor: Colors.deepPurple.shade50,
                          contentPadding: const EdgeInsets.all(0),
                          shape: RoundedRectangleBorder(
                            borderRadius: BorderRadius.circular(5),
                          ),
                          title: Text(ProductTypeEnum.Deliverable.name),
                          value: _productTypeEnum,
                          groupValue: ProductTypeEnum.Deliverable,
                          onChanged: (val) {
                            setState(() {
                              _productTypeEnum = ProductTypeEnum.Deliverable;
                            });
                          }),
                    ),
                  ),
                ],
              ),
              const SizedBox(
                height: 40,
              ),
              buildOutlinedButton(context)
            ],
          ),
        ));
  }

  Expanded buildRadio(
      BuildContext context, ProductTypeEnum typeEnum, double d) {
    return Expanded(
      child: Container(
        margin: EdgeInsets.only(left: d),
        color: Colors.deepPurple.shade50,
        child: Row(
          children: [
            ListTileTheme(
                // tileColor: Colors.lightBlueAccent,
                horizontalTitleGap: 0,
                child: Radio<ProductTypeEnum>(
                    visualDensity: VisualDensity.compact,
                    value: typeEnum,
                    groupValue: _productTypeEnum,
                    onChanged: (val) {
                      setState(() {
                        _productTypeEnum = typeEnum;
                      });
                    })),
            GestureDetector(
                onTap: () {
                  setState(() {
                    _productTypeEnum = typeEnum;
                  });
                },
                child: Text(typeEnum.name)),
          ],
        ),
      ),
    );
  }

  OutlinedButton buildOutlinedButton(BuildContext context) {
    return OutlinedButton(
      onPressed: () {},
      style: OutlinedButton.styleFrom(minimumSize: const Size(100, 50)),
      child: const Text(
        "SubmitForm",
        style: TextStyle(fontSize: 18),
      ),
    );
  }
}


