# app/src/main/java/com/android_labs/uiloverquizapp/LeaderListAdapter.kt
class LeaderAdapter(private val list: List<UserModel>) : RecyclerView.Adapter<LeaderAdapter.ViewHolder>() {

    override fun onCreateViewHolder(
        parent: ViewGroup,
        viewType: Int
    ): LeaderAdapter.ViewHolder {
        return ViewHolder(ViewholderLeadersBinding.inflate(LayoutInflater.from(parent.context), parent, false))
    }

    override fun getItemCount(): Int {
        return list.size
    }

    override fun onBindViewHolder(holder: LeaderAdapter.ViewHolder, position: Int) {
        holder.bind(list[position])
    }

    inner class ViewHolder(private val binding: ViewholderLeadersBinding) : RecyclerView.ViewHolder(binding.root) {

        fun bind(model: UserModel) {
            binding.textView15.text = model.id.toString();
            binding.textView18.text = model.name
            binding.textView19.text = model.score.toString()

            var drawableResourceId: Int = binding.root.resources.getIdentifier(
                model.pic,
                "drawable", binding.root.context.packageName
            )

            // 设置头像
            Glide.with(binding.root.context).load(drawableResourceId).into(binding.imageView1221)
        }
    }
}


# app/src/main/java/com/android_labs/uiloverquizapp/LeaderActivity.kt
class LeaderActivity : AppCompatActivity() {

    private lateinit var binding: ActivityLeaderBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        binding = ActivityLeaderBinding.inflate(layoutInflater)

        // 绑定视图
        this.bindingView(loadDataTop())

        // 设置视图
        this.setContentView(binding.root)

        // 修改状态栏颜色
        this@LeaderActivity.window.statusBarColor = ContextCompat.getColor(this@LeaderActivity, R.color.grey)
    }

    private fun bindingView(data: MutableList<UserModel>) {

        binding.apply {
            textView1601.text = data[0].score.toString()
            textView1602.text = data[1].score.toString()
            textView1603.text = data[2].score.toString()

            titleTop1Txt.text = data[0].name
            titleTop2Txt.text = data[1].name
            titleTop3Txt.text = data[2].name

            Glide.with(root.context).load(root.resources.getIdentifier(
                data[0].pic,"drawable", root.context.packageName
            )).into(imageView1201)

            Glide.with(root.context).load(root.resources.getIdentifier(
                data[1].pic,"drawable", root.context.packageName
            )).into(imageView12002)

            Glide.with(root.context).load(root.resources.getIdentifier(
                data[2].pic,"drawable", root.context.packageName
            )).into(imageView12)

            bottomMenu.setItemSelected(R.id.board)

            bottomMenu.setOnItemSelectedListener {
                if (it == R.id.home) {
                    startActivity(Intent(this@LeaderActivity, MainActivity::class.java))
                }
            }

            leaderView.apply {
                layoutManager = LinearLayoutManager(this@LeaderActivity)
                adapter = LeaderAdapter(loadDataList())
            }
        }
    }

    private fun loadDataTop(): MutableList<UserModel> {
        return mutableListOf(
            UserModel(1, "Sophia", "person1", 4850),
            UserModel(2, "Daniel", "person2", 4560),
            UserModel(3, "James", "person3", 3873),
        )
    }

    private fun loadDataList(): MutableList<UserModel> {
        return mutableListOf(
            UserModel(4, "John Smith", "person4", 3250),
            UserModel(5, "Emily Johnson", "person5", 3015),
            UserModel(6, "David Brown", "person6", 2970),
            UserModel(7, "Sarah Wilson", "person7", 2870),
            UserModel(8, "Michael Davis", "person8", 2670),
            UserModel(9, "Sarah Wilson", "person9", 2380),
        )
    }
}


# app/src/main/java/com/android_labs/uiloverquizapp/QuestionModel.kt
data class QuestionModel(
    val id: Int,
    val question: String?,
    val answer_1: String?,
    val answer_2: String?,
    val answer_3: String?,
    val answer_4: String?,
    val correctAnswer: String?,
    var score: Int,
    val picPath: String?,
    var clickedAnswer: String?
): Parcelable {
    constructor(parcel: Parcel) : this(
        parcel.readInt(),
        parcel.readString(),
        parcel.readString(),
        parcel.readString(),
        parcel.readString(),
        parcel.readString(),
        parcel.readString(),
        parcel.readInt(),
        parcel.readString(),
        parcel.readString()
    ) {
    }

    override fun writeToParcel(parcel: Parcel, flags: Int) {
        parcel.writeInt(id)
        parcel.writeString(question)
        parcel.writeString(answer_1)
        parcel.writeString(answer_2)
        parcel.writeString(answer_3)
        parcel.writeString(answer_4)
        parcel.writeString(correctAnswer)
        parcel.writeInt(score)
        parcel.writeString(picPath)
        parcel.writeString(clickedAnswer)
    }

    override fun describeContents(): Int {
        return 0
    }

    companion object CREATOR : Parcelable.Creator<QuestionModel> {
        override fun createFromParcel(parcel: Parcel): QuestionModel {
            return QuestionModel(parcel)
        }

        override fun newArray(size: Int): Array<QuestionModel?> {
            return arrayOfNulls(size)
        }
    }
}


# app/src/main/java/com/android_labs/uiloverquizapp/QuestionAdapter.kt
class QuestionAdapter(
    private val correctAnswer: String,
    private val users: MutableList<String> = mutableListOf(),
    private var returnScore: Score,
): RecyclerView.Adapter<QuestionAdapter.ViewHolder>() {

    interface Score {
        fun amount(number: Int, clickedAnser: String)
    }

    private val differCallback = object: DiffUtil.ItemCallback<String>() {
        override fun areItemsTheSame(oldItem: String, newItem: String): Boolean {
            return oldItem == newItem
        }

        override fun areContentsTheSame(oldItem: String, newItem: String): Boolean {
            return oldItem == newItem
        }
    }

    val differ = AsyncListDiffer(this, differCallback);
    private lateinit var binding: ViewholderQuestionBinding

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        var inflater = LayoutInflater.from(parent.context);
        binding = ViewholderQuestionBinding.inflate(
            inflater, parent, false
        )

        return ViewHolder()
    }

    override fun getItemCount(): Int {
        return differ.currentList.size
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        var binding = ViewholderQuestionBinding.bind(holder.itemView)
        binding.answerTxt.text = differ.currentList[position]

        var correctPos = 0

        when(correctAnswer) {
            "a" -> correctPos = 0
            "b" -> correctPos = 1
            "c" -> correctPos = 2
            "d" -> correctPos = 3
        }

        if (position == 4) {
            binding.root.visibility = View.GONE
        }

        holder.itemView.setOnClickListener {
            var str = ""

            when (position) {
                0 -> "a"
                1 -> "b"
                2 -> "c"
                3 -> "d"
            }

            users.add(4, str)

            notifyDataSetChanged()

            if (correctPos == position) {
                binding.answerTxt.setBackgroundResource(R.drawable.green_background)
                binding.answerTxt.setTextColor(ContextCompat.getColor(binding.root.context, R.color.white))
                val drawable = ContextCompat.getDrawable(binding.root.context, R.drawable.tick)
                binding.answerTxt.setCompoundDrawablesRelativeWithIntrinsicBounds(null, null, drawable, null)
                returnScore.amount(5, str)
            } else {
                binding.answerTxt.setBackgroundResource(R.drawable.red_background)
                binding.answerTxt.setTextColor(ContextCompat.getColor(binding.root.context, R.color.white))
                val drawable = ContextCompat.getDrawable(binding.root.context, R.drawable.thieves)
                binding.answerTxt.setCompoundDrawablesRelativeWithIntrinsicBounds(null, null, drawable, null)
                returnScore.amount(0, str)
            }
        }

        if (differ.currentList.size == 5) {
            holder.itemView.setOnClickListener(null)
        }
    }

    inner class ViewHolder : RecyclerView.ViewHolder(binding.root) {

    }
}


# app/src/main/java/com/android_labs/uiloverquizapp/QuestionActivity.kt
class QuestionActivity : AppCompatActivity(), QuestionAdapter.Score {

    private lateinit var binding: ActivityQuestionBinding

    private var position = 0
    private var receivedList: MutableList<QuestionModel> = mutableListOf()
    private var allScore = 0

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        binding = ActivityQuestionBinding.inflate(layoutInflater)

        setContentView(binding.root)

        val window: Window = this@QuestionActivity.window

        // 修改状态栏颜色
        window.statusBarColor = ContextCompat.getColor(this@QuestionActivity, R.color.grey)

        receivedList = intent.getParcelableArrayListExtra<QuestionModel>("list")!!.toMutableList()

        binding.apply {
            backBtn.setOnClickListener {
                finish()
            }

            progressBar.progress = 1

            questionTxt.text =receivedList[position].question

            val drawableResourceId: Int = binding.root.resources.getIdentifier(
                receivedList[position].picPath, "drawable", binding.root.context.packageName
            )

            Glide.with(this@QuestionActivity)
                .load(drawableResourceId)
                .centerCrop().apply(RequestOptions.bitmapTransform(RoundedCorners(60)))
                .into(pic)

            loadAnswers()

            rightArrow.setOnClickListener {
                if (progressBar.progress == 10) {
                    val intent = Intent(this@QuestionActivity, ScoreActivity::class.java)
                    intent.putExtra("Score", allScore)
                    startActivity(intent)
                    finish()
                    return@setOnClickListener
                }

                position++
                progressBar.progress = progressBar.progress + 1
                questionNumberTxt.text = "Question " + progressBar.progress + "/10"
                questionTxt.text = receivedList[position].question

                val drawableResourceId: Int = binding.root.resources.getIdentifier(
                    receivedList[position].picPath, "drawable", binding.root.context.packageName
                )

                Glide.with(this@QuestionActivity)
                    .load(drawableResourceId)
                    .centerCrop().apply(RequestOptions.bitmapTransform(RoundedCorners(60)))
                    .into(pic)

                loadAnswers()
            }

            leftArrow.setOnClickListener {
                if (progressBar.progress == 1) {
                    return@setOnClickListener
                }

                position--
                progressBar.progress = progressBar.progress - 1
                questionNumberTxt.text = "Question " + progressBar.progress + "/10"
                questionTxt.text = receivedList[position].question

                val drawableResourceId: Int = binding.root.resources.getIdentifier(
                    receivedList[position].picPath, "drawable", binding.root.context.packageName
                )

                Glide.with(this@QuestionActivity)
                    .load(drawableResourceId)
                    .centerCrop().apply(RequestOptions.bitmapTransform(RoundedCorners(60)))
                    .into(pic)

                loadAnswers()
            }
        }
    }

    private fun loadAnswers() {
        val users: MutableList<String> = mutableListOf(
            receivedList[position].answer_1.toString(),
            receivedList[position].answer_2.toString(),
            receivedList[position].answer_3.toString(),
            receivedList[position].answer_4.toString()
        )

        if (receivedList[position].clickedAnswer != null) {
            users.add(receivedList[position].clickedAnswer.toString())
        }

        val questionAdapter by lazy {
            QuestionAdapter(
                receivedList[position].correctAnswer.toString(),
                users,
                this
            )
        }

        questionAdapter.differ.submitList(users)

        binding.questionView.apply {
            layoutManager = LinearLayoutManager(this@QuestionActivity)
            adapter = questionAdapter
        }
    }

    override fun amount(number: Int, clickedAnser: String) {
        allScore += number
        receivedList[position].clickedAnswer = clickedAnser
    }
}

