# app/src/main/java/com/android_labs/travelapp/IntroActivity.kt
class IntroActivity : AppCompatActivity() {

    private lateinit var getStarted: ConstraintLayout

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_intro)

        this.getStarted = findViewById(R.id.getStarted)

        this.getStarted.setOnClickListener {
            startActivity(Intent(this@IntroActivity, MainActivity::class.java))
        }
    }
}


# app/src/main/java/com/android_labs/travelapp/MainActivity.kt
class MainActivity : AppCompatActivity() {

    private lateinit var viewPopular: RecyclerView
    private lateinit var viewCategory: RecyclerView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        this.viewPopular = findViewById(R.id.viewPopular)
        this.viewCategory = findViewById(R.id.viewCategory)

        this.viewPopular.layoutManager = LinearLayoutManager(this@MainActivity)
        this.viewCategory.layoutManager = LinearLayoutManager(this@MainActivity)

        this.viewPopular.adapter = PopularAdapter(loadPopulars())
        this.viewCategory.adapter = CategoryAdapter(loadCategories())

    }

    private fun loadCategories(): List<Category> {
        return emptyList<Category>()
    }

    private fun loadPopulars(): List<City> {
        return emptyList<City>()
    }
}


