# app/src/main/java/com/android_labs/travelapp/CategoryAdapter.kt
class CategoryAdapter(var list: List<Category>): RecyclerView.Adapter<CategoryAdapter.ViewHoldder>() {

    inner class ViewHoldder(private var view: View): RecyclerView.ViewHolder(view) {

        private var cateTitleTxt = view.findViewById<TextView>(R.id.cateTitleTxt)
        private var cateImg = view.findViewById<ImageView>(R.id.cateImg)

        fun bind(item: Category) {
            this.cateTitleTxt.text = item.title
            Glide.with(view.context)
                 .load(getResourceId(view, item.pic))
                 .transform(CenterCrop(), GranularRoundedCorners(40.0f, 40.0f, 40.0f, 40.0f))
                 .into(this.cateImg)
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHoldder {
        var view = LayoutInflater.from(parent.context).inflate(R.layout.viewholder_category, parent, false)
        return ViewHoldder(view)
    }

    override fun getItemCount(): Int {
        return list.size
    }

    override fun onBindViewHolder(holder: ViewHoldder, position: Int) {
        holder.bind(list[position])
    }

    fun getResourceId(view: View, name: String): Int {
        return view.resources.getIdentifier(name, "drawable", view.context.packageName)
    }
}


# app/src/main/java/com/android_labs/travelapp/PopularAdapter.kt
class PopularAdapter(var list: List<City>) : RecyclerView.Adapter<PopularAdapter.ViewHolder>() {

    inner class ViewHolder(private var view: View): RecyclerView.ViewHolder(view) {

        private var popularImg = view.findViewById<ImageView>(R.id.popularImg)
        private var popularTitleTxt = view.findViewById<TextView>(R.id.popularTitleTxt)
        private var locationTxt = view.findViewById<TextView>(R.id.locationTxt)
        private var scoreTxt = view.findViewById<TextView>(R.id.scoreTxt)

        private lateinit var city: City

        init {
            view.setOnClickListener {
                var intent = Intent(view.context, DetailsActivity::class.java)
                intent.putExtra("city", city)
                view.context.startActivity(intent)
            }
        }

        fun bind(city: City) {
            this.city = city

            this.popularTitleTxt.text = city.title
            this.locationTxt.text = city.location
            this.scoreTxt.text = city.score.toString()

            Glide.with(view.context)
                 .load(getResourceId(view, city.pic))
                 .transform(CenterCrop(), GranularRoundedCorners(40.0f, 40.0f, 40.0f, 40.0f))
                 .into(this.popularImg)
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        var view = LayoutInflater.from(parent.context).inflate(R.layout.viewholder_popular, parent, false)
        return ViewHolder(view)
    }

    override fun getItemCount(): Int {
        return list.size
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        holder.bind(list[position])
    }

    fun getResourceId(view: View, name: String): Int {
        return view.resources.getIdentifier(name, "drawable", view.context.packageName)
    }
}


# app/src/main/java/com/android_labs/travelapp/DetailsActivity.kt
class DetailsActivity : AppCompatActivity() {

    private lateinit var titleTxt: TextView
    private lateinit var locationTxt: TextView
    private lateinit var bedTxt: TextView
    private lateinit var guideTxt: TextView
    private lateinit var wifiTxt: TextView
    private lateinit var descriptionTxt: TextView
    private lateinit var scoreTxt: TextView
    private lateinit var priceTxt: TextView
    private lateinit var picImg: ImageView

    private lateinit var backBtn: ImageView

    private lateinit var city: City

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        setContentView(R.layout.activity_details)

        this.titleTxt = findViewById(R.id.titleTxt)
        this.locationTxt = findViewById(R.id.locationTxt)
        this.bedTxt = findViewById(R.id.bedTxt)
        this.guideTxt = findViewById(R.id.guideTxt)
        this.wifiTxt = findViewById(R.id.wifiTxt)
        this.descriptionTxt = findViewById(R.id.descriptionTxt)
        this.scoreTxt = findViewById(R.id.scoreTxt)
        this.priceTxt = findViewById(R.id.priceTxt)
        this.picImg = findViewById(R.id.picImg)

        this.backBtn = findViewById(R.id.backBtn)

        this.backBtn.setOnClickListener {

        }

        this.city = getSerializable(this, "city", City::class.java)

        this.titleTxt.text = this.city.title
        this.locationTxt.text = this.city.location
        this.bedTxt.text = this.city.bed.toString()
        this.descriptionTxt.text = this.city.description
        this.scoreTxt.text = this.city.score.toString()
        this.priceTxt.text = this.city.price.toString()

        if (this.city.guide) {
            this.guideTxt.text = "Guide"
        } else {
            this.guideTxt.text = "No Guide"
        }

        if (this.city.wifi) {
            this.wifiTxt.text = "Wifi"
        } else {
            this.wifiTxt.text = "No Wifi"
        }

        Glide.with(this).load(getResourceId(this.city.pic)).into(this.picImg)
    }

    private fun <T : Serializable?> getSerializable(
        activity: Activity,
        name: String,
        clazz: Class<T>
    ): T {
        return if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU)
            activity.intent.getSerializableExtra(name, clazz)!!
        else
            activity.intent.getSerializableExtra(name) as T
    }

    private fun getResourceId(name: String): Int {
        return this.resources.getIdentifier(name, "drawable", this.packageName)
    }
}
