# app/src/main/java/com/android_labs/weatherapp/HourlyAdapter.kt
class HourlyAdapter(val list: List<Hourly>): RecyclerView.Adapter<HourlyAdapter.ViewHolder>() {

    inner class ViewHolder(private var view: View): RecyclerView.ViewHolder(view) {

        val hourTxt = view.findViewById<TextView>(R.id.hourTxt)
        val picImage = view.findViewById<ImageView>(R.id.pic)
        val tmpTxt = view.findViewById<TextView>(R.id.tmpTxt)

        fun bind(item: Hourly) {
            this.hourTxt.text = item.hour
            this.tmpTxt.text = item.temp.toString()

            Glide.with(view.context).load(getResourceId(view, item.picPath)).into(picImage)
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        var inflate = LayoutInflater.from(parent.context).inflate(R.layout.hourly_viewholder, parent, false)
        return ViewHolder(inflate)
    }

    override fun getItemCount(): Int {
        return list.size
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        holder.bind(list[position])
    }

    fun getResourceId(view: View, name: String): Int {
        return view.resources.getIdentifier(name, "drawable", view.context.packageName)
    }
}


# app/src/main/java/com/android_labs/weatherapp/MainActivity.kt
class MainActivity : AppCompatActivity() {

    private lateinit var listView: RecyclerView
    private lateinit var next7Day: TextView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        this.next7Day = findViewById(R.id.next7Day)
        this.listView = findViewById(R.id.listView)
        this.listView.layoutManager = LinearLayoutManager(this@MainActivity, LinearLayoutManager.HORIZONTAL, false)
        this.listView.adapter = HourlyAdapter(loadData())

        this.next7Day.setOnClickListener {
            startActivity(Intent(this@MainActivity, FutureActivity::class.java))
        }
    }

    fun loadData(): List<Hourly> {
        return mutableListOf(
            Hourly("9 pm", 28, "cloudy"),
            Hourly("11 pm", 29, "sunny"),
            Hourly("12 pm", 30, "wind"),
            Hourly("1 am", 29, "rainy"),
            Hourly("2 am", 27, "storm"),
        )
    }
}


# app/src/main/java/com/android_labs/weatherapp/FutureAdapter.kt
class FutureAdapter(var list: List<Future>): RecyclerView.Adapter<FutureAdapter.ViewHoldder>() {

    inner class ViewHoldder(private var view: View): RecyclerView.ViewHolder(view) {

        private var dayTxt = view.findViewById<TextView>(R.id.dayTxt)
        private var pic = view.findViewById<ImageView>(R.id.pic)
        private var statusTxt = view.findViewById<TextView>(R.id.statusTxt)
        private var lowTxt = view.findViewById<TextView>(R.id.lowTxt)
        private var higtTxt = view.findViewById<TextView>(R.id.higtTxt)

        fun bind(item: Future) {
            this.dayTxt.text = item.day
            this.statusTxt.text = item.status
            this.lowTxt.text = item.lowTemp.toString()
            this.higtTxt.text = item.highTemp.toString()

            Glide.with(view.context).load(getResourceId(view, item.picPath)).into(this.pic)
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHoldder {
        var inflater = LayoutInflater.from(parent.context).inflate(R.layout.future_viewholder, parent, false)
        return ViewHoldder(inflater)
    }

    override fun getItemCount(): Int {
        return list.size
    }

    override fun onBindViewHolder(holder: ViewHoldder, position: Int) {
        holder.bind(list[position])
    }

    fun getResourceId(view: View, name: String): Int {
        return view.resources.getIdentifier(name, "drawable", view.context.packageName)
    }
}


# app/src/main/java/com/android_labs/weatherapp/FutureActivity.kt
class FutureActivity : AppCompatActivity() {

    private lateinit var futureList: RecyclerView
    private lateinit var backBtn: ConstraintLayout

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_future)

        this.backBtn = findViewById(R.id.backBtun)
        this.futureList = findViewById(R.id.viewList2)

        this.futureList.layoutManager = LinearLayoutManager(this@FutureActivity, LinearLayoutManager.VERTICAL, false)
        this.futureList.adapter = FutureAdapter(loadData())

        this.backBtn.setOnClickListener {
            startActivity(Intent(this@FutureActivity, MainActivity::class.java))
        }
    }

    private fun loadData(): List<Future> {
        return mutableListOf(
            Future("Sat", "storm", "Storm", 25, 10),
            Future("Sun", "cloudy", "Cloudy", 24, 16),
            Future("Mon", "windy", "Windy", 29, 15),
            Future("Tue", "cloudy_sunny", "Cloudy_Sunny", 22, 13),
            Future("Wen", "sunny", "Sunny", 28, 11),
            Future("Thu", "rainy", "Rainy", 23, 12),
        )
    }
}
