import RPi.GPIO as GPIO from time import sleep
GPIO.setmode(GPIO.BCM) GPIO.setup(17, GPIO.OUT)
print "All set in Python, Let's Blink"
for i in range(1, 10): GPIO.output(17, GPIO.HIGHT) sleep(1) GPIO.output(17, GPIO.LOW) sleep(0.5)
GPIO.cleanup();


#include<stdio.h>
#include<wiringPi.h>  /* 引入GPIO的所有功能 */
#include<time.h>

void main() {

  wiringPiSetup();
  pinMode(17, OUTPUT);

  printf("Using wiringPi, Blink Blink!\n");
  printf("Press ctrl C to exit");

  for (;;) {
      digitalWrite(17, 1);
      delay(1000);
      digitalWrite(17, 0);
      delay(500);
  }

}


