char phone_no[] = "+8618500180000"

void setup() {

  Serial.begin(9600);
  delay(300);


  Serial.println("AT+CMGF=1");      // to enable Text mode
  delay(2000);

  Serial.print("AT+CMGS=\"");
  Serial.print(phone_no);
  Serial.write(0x22);               // 22 - ASCII for "
  Serial.write(0x0D);               // 0D - ASCII for CR
  Serial.write(0x0A);               // 0A - ASCII for LF (hex equivalent of newline)
  delay(2000);

  Serial.print("GSM test message!");
  delay(500);

  Serial.println(char(26));         // the ASCII code of the ctrl+z is 26 (or 0x1A)

}

void loop() {

}
